public class Exe04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 1;

		System.out.println("While:");

		while (i <= 10) {
			System.out.println(i);
			i++;
		}
		i = 1;

		System.out.println("Do While:");

		do {
			System.out.println(i);
			i++;
		} while (i <= 10);

		System.out.println("For:");

		for (int j = 1; j <= 10; j++) {
			System.out.println(j);
		}

	}

}
